254â†’async function prepareAttestationRequest(
255â†’  txHash: string,
256â†’  requiredConfirmations: number
257â†’): Promise<string> {
258â†’  const sourceNetwork = FdcSourceNetwork.ETH;
259â†’  const sourceId = toBytes32String(sourceNetwork === "ETH" ? "testETH" : sourceNetwork);
260â†’  const attestationType = toBytes32String("EVMTransaction");
261â†’
262â†’  const requestBody = {
263â†’    transactionHash: txHash,
264â†’    requiredConfirmations: String(requiredConfirmations),
265â†’    provideInput: true,
266â†’    listEvents: true,
267â†’    logIndices: [],
268â†’  };
269â†’
270â†’  const url = `${VERIFIER_BASE_URL}verifier/eth/EVMTransaction/prepareRequest`;
271â†’
272â†’  const response = await fetch(url, {
273â†’    method: "POST",
274â†’    headers: {
275â†’      "Content-Type": "application/json",
276â†’      "X-API-KEY": API_KEY,
277â†’    },
278â†’    body: JSON.stringify({ attestationType, sourceId, requestBody }),
279â†’  });
280â†’
281â†’  if (!response.ok) {
282â†’    const text = await response.text();
283â†’    throw new Error(
284â†’      `Verifier API error (${response.status}): ${text}\n` +
285â†’        `This may indicate the transaction hash is invalid or doesn't exist on Sepolia.`
286â†’    );
287â†’  }
288â†’
289â†’  const data = await response.json();
290â†’  if (data.status === "INVALID" || !data.abiEncodedRequest) {
291â†’    throw new Error(
292â†’      `Verifier rejected request: ${JSON.stringify(data)}\n` +
293â†’        `Possible causes: invalid tx hash, insufficient confirmations, or tx doesn't exist.`
294â†’    );
295â†’  }
296â†’
297â†’  return data.abiEncodedRequest;
298â†’}
