304â†’async function submitAttestationRequest(
305â†’  signer: ethers.Wallet,
306â†’  abiEncodedRequest: string
307â†’): Promise<{ coston2TxHash: string; votingRoundId: bigint }> {
308â†’  const fdcHub = new ethers.Contract(FDC_HUB_ADDRESS, FDC_HUB_ABI, signer);
309â†’  const feeConfig = new ethers.Contract(
310â†’    FDC_FEE_CONFIG_ADDRESS,
311â†’    FDC_FEE_CONFIG_ABI,
312â†’    signer.provider
313â†’  );
314â†’
315â†’  let fee: bigint;
316â†’  try {
317â†’    fee = await feeConfig.getRequestFee(abiEncodedRequest);
318â†’    console.log(`  Attestation fee: ${ethers.formatEther(fee)} C2FLR`);
319â†’  } catch {
320â†’    fee = ethers.parseEther("0.5");
321â†’    console.log(`  Using default fee: ${ethers.formatEther(fee)} C2FLR`);
322â†’  }
323â†’
324â†’  const balance = await signer.provider!.getBalance(signer.address);
325â†’  if (balance < fee) {
326â†’    throw new Error(
327â†’      `Insufficient C2FLR balance!\n` +
328â†’        `  Have: ${ethers.formatEther(balance)} C2FLR\n` +
329â†’        `  Need: ${ethers.formatEther(fee)} C2FLR\n` +
330â†’        `  Get testnet funds at: https://faucet.flare.network/coston2`
331â†’    );
332â†’  }
333â†’
334â†’  const tx = await fdcHub.requestAttestation(abiEncodedRequest, { value: fee });
335â†’  console.log(`  Submitted Coston2 tx: ${tx.hash}`);
336â†’
337â†’  const receipt = await tx.wait(1);
338â†’  if (!receipt || receipt.status !== 1) {
339â†’    throw new Error(`Transaction reverted: ${tx.hash}`);
340â†’  }
341â†’
342â†’  const block = await signer.provider!.getBlock(receipt.blockNumber);
343â†’  if (!block) throw new Error("Failed to get block");
344â†’
345â†’  const votingRoundId =
346â†’    (BigInt(block.timestamp) - FIRST_VOTING_ROUND_START_TS) / VOTING_EPOCH_DURATION_S;
347â†’
348â†’  return { coston2TxHash: tx.hash, votingRoundId };
349â†’}
