355â†’async function waitForProof(
356â†’  votingRoundId: bigint,
357â†’  abiEncodedRequest: string,
358â†’  maxWaitMs: number = 10 * 60 * 1000
359â†’): Promise<ProofData> {
360â†’  const url = `${DA_LAYER_URL}api/v0/fdc/get-proof-round-id-bytes`;
361â†’  const startTime = Date.now();
362â†’  let delay = 10_000;
363â†’  let attempts = 0;
364â†’
365â†’  console.log(`  Waiting initial ~90s for voting round to complete...`);
366â†’  await sleep(95_000);
367â†’
368â†’  while (true) {
369â†’    attempts++;
370â†’    const elapsed = Math.floor((Date.now() - startTime) / 1000);
371â†’
372â†’    if (Date.now() - startTime > maxWaitMs) {
373â†’      throw new Error(
374â†’        `Timed out after ${elapsed}s waiting for proof (voting round ${votingRoundId}).`
375â†’      );
376â†’    }
377â†’
378â†’    try {
379â†’      const response = await fetch(url, {
380â†’        method: "POST",
381â†’        headers: {
382â†’          "Content-Type": "application/json",
383â†’          "X-API-KEY": API_KEY,
384â†’        },
385â†’        body: JSON.stringify({
386â†’          votingRoundId: Number(votingRoundId),
387â†’          requestBytes: abiEncodedRequest,
388â†’        }),
389â†’      });
390â†’
391â†’      if (response.ok) {
392â†’        const data = await response.json();
393â†’        if (data.proof && data.response) {
394â†’          console.log(`  Proof available after ${elapsed}s (${attempts} attempts)`);
395â†’          return data as ProofData;
396â†’        }
397â†’      }
398â†’
399â†’      console.log(
400â†’        `  [${elapsed}s] Attempt ${attempts}: proof not yet available (status ${response.status}), ` +
401â†’          `retrying in ${Math.floor(delay / 1000)}s...`
402â†’      );
403â†’    } catch (err: unknown) {
404â†’      const msg = err instanceof Error ? err.message : String(err);
405â†’      console.log(
406â†’        `  [${elapsed}s] Attempt ${attempts}: ${msg}, retrying in ${Math.floor(delay / 1000)}s...`
407â†’      );
408â†’    }
409â†’
410â†’    await sleep(delay);
411â†’    delay = Math.min(delay * 1.5, 30_000);
412â†’  }
413â†’}
414â†’
415â†’// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
416â†’// Step 5: Verify Proof On-Chain
417â†’// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
