419â†’async function verifyOnChain(
420â†’  provider: ethers.JsonRpcProvider,
421â†’  proofData: ProofData
422â†’): Promise<boolean> {
423â†’  const contract = new ethers.Contract(
424â†’    FDC_VERIFICATION_ADDRESS,
425â†’    FDC_VERIFICATION_ABI,
426â†’    provider
427â†’  );
428â†’
429â†’  const resp = proofData.response;
430â†’  const body = resp.responseBody;
431â†’
432â†’  const proofStruct = {
433â†’    merkleProof: proofData.proof,
434â†’    data: {
435â†’      attestationType: resp.attestationType,
436â†’      sourceId: resp.sourceId,
437â†’      votingRound: BigInt(resp.votingRound),
438â†’      lowestUsedTimestamp: BigInt(resp.lowestUsedTimestamp),
439â†’      requestBody: {
440â†’        transactionHash: resp.requestBody.transactionHash,
441â†’        requiredConfirmations: Number(resp.requestBody.requiredConfirmations),
442â†’        provideInput: resp.requestBody.provideInput,
443â†’        listEvents: resp.requestBody.listEvents,
444â†’        logIndices: (resp.requestBody.logIndices || []).map(Number),
445â†’      },
446â†’      responseBody: {
447â†’        blockNumber: BigInt(body.blockNumber),
448â†’        timestamp: BigInt(body.timestamp),
449â†’        sourceAddress: body.sourceAddress,
450â†’        isDeployment: body.isDeployment,
451â†’        receivingAddress: body.receivingAddress || ethers.ZeroAddress,
452â†’        value: BigInt(body.value),
453â†’        input: body.input || "0x",
454â†’        status: Number(body.status),
455â†’        events: (body.events || []).map((e) => ({
456â†’          logIndex: Number(e.logIndex),
457â†’          emitterAddress: e.emitterAddress,
458â†’          topics: e.topics,
459â†’          data: e.data || "0x",
460â†’          removed: e.removed || false,
461â†’        })),
462â†’      },
463â†’    },
464â†’  };
465â†’
466â†’  return await contract.verifyEVMTransaction(proofStruct);
467â†’}
