598â†’  console.log(`  Verification PASSED`);
599â†’
600â†’  // Step 6: Relay to Sapphire
601â†’  console.log(`\n${"â”€".repeat(70)}`);
602â†’  console.log(`Step 6: Relay deposit to Sapphire accounting contract`);
603â†’  console.log(`${"â”€".repeat(70)}`);
604â†’  await relayToSapphire(accountingContract, proofData);
605â†’
606â†’  console.log(`\n${"â•".repeat(70)}`);
607â†’  console.log(`  DONE â€” Deposit verified via FDC and credited on Sapphire`);
608â†’  console.log(`${"â•".repeat(70)}\n`);
609â†’}
