  console.log(`${"â”€".repeat(70)}`);
  const sepoliaTxHash = await sendSepoliaDeposit(sepoliaSigner, requiredConfirmations);

  // Step 1: Deploy or connect to FdcAccounting on Sapphire
  console.log(`\n${"â”€".repeat(70)}`);
