173‚Üífunction sleep(ms: number): Promise<void> {
174‚Üí  return new Promise((resolve) => setTimeout(resolve, ms));
175‚Üí}
176‚Üí
177‚Üí/**
178‚Üí * Encodes a string to bytes32 hex (right-padded with zeros)
179‚Üí * e.g., "testETH" -> "0x7465737445544800000000000000000000000000000000000000000000000000"
180‚Üí */
181‚Üífunction toBytes32String(s: string): string {
182‚Üí  const bytes = new TextEncoder().encode(s);
183‚Üí  if (bytes.length > 32) throw new Error(`String too long for bytes32: ${s}`);
184‚Üí  const hex = Array.from(bytes)
185‚Üí    .map((b) => b.toString(16).padStart(2, "0"))
186‚Üí    .join("");
187‚Üí  return "0x" + hex.padEnd(64, "0");
188‚Üí}
189‚Üí
190‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
191‚Üí// Step 1: Prepare Attestation Request
192‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
193‚Üí
194‚Üíasync function prepareAttestationRequest(
195‚Üí  txHash: string,
196‚Üí  requiredConfirmations: number
197‚Üí): Promise<string> {
198‚Üí  // Use SDK's FdcSourceNetwork enum to map to the correct source ID
199‚Üí  // For Coston2 (testnet), ETH maps to "testETH"
200‚Üí  const sourceNetwork = FdcSourceNetwork.ETH;
201‚Üí  const sourceId = toBytes32String(sourceNetwork === "ETH" ? "testETH" : sourceNetwork);
202‚Üí  const attestationType = toBytes32String("EVMTransaction");
203‚Üí
204‚Üí  const requestBody = {
205‚Üí    transactionHash: txHash,
206‚Üí    requiredConfirmations: String(requiredConfirmations),
207‚Üí    provideInput: true,
208‚Üí    listEvents: true,
209‚Üí    logIndices: [], // empty = all events
210‚Üí  };
211‚Üí
212‚Üí  const url = `${VERIFIER_BASE_URL}verifier/eth/EVMTransaction/prepareRequest`;
213‚Üí
214‚Üí  const response = await fetch(url, {
215‚Üí    method: "POST",
216‚Üí    headers: {
217‚Üí      "Content-Type": "application/json",
218‚Üí      "X-API-KEY": API_KEY,
219‚Üí    },
220‚Üí    body: JSON.stringify({
221‚Üí      attestationType,
222‚Üí      sourceId,
223‚Üí      requestBody,
224‚Üí    }),
225‚Üí  });
226‚Üí
227‚Üí  if (!response.ok) {
228‚Üí    const text = await response.text();
229‚Üí    throw new Error(
230‚Üí      `Verifier API error (${response.status}): ${text}\n` +
231‚Üí        `This may indicate the transaction hash is invalid or doesn't exist on Sepolia.`
232‚Üí    );
233‚Üí  }
234‚Üí
235‚Üí  const data = await response.json();
236‚Üí
237‚Üí  if (data.status === "INVALID" || !data.abiEncodedRequest) {
238‚Üí    throw new Error(
239‚Üí      `Verifier rejected request: ${JSON.stringify(data)}\n` +
240‚Üí        `Possible causes: invalid tx hash, insufficient confirmations, or tx doesn't exist.`
241‚Üí    );
242‚Üí  }
243‚Üí
244‚Üí  return data.abiEncodedRequest;
245‚Üí}
246‚Üí
247‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
248‚Üí// Step 2: Submit Attestation Request to FdcHub
249‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
250‚Üí
251‚Üíasync function submitAttestationRequest(
252‚Üí  signer: ethers.Wallet,
253‚Üí  abiEncodedRequest: string
254‚Üí): Promise<{ coston2TxHash: string; votingRoundId: bigint }> {
255‚Üí  const fdcHub = new ethers.Contract(FDC_HUB_ADDRESS, FDC_HUB_ABI, signer);
256‚Üí  const feeConfig = new ethers.Contract(
257‚Üí    FDC_FEE_CONFIG_ADDRESS,
258‚Üí    FDC_FEE_CONFIG_ABI,
259‚Üí    signer.provider
260‚Üí  );
261‚Üí
262‚Üí  // Get required attestation fee
263‚Üí  let fee: bigint;
264‚Üí  try {
265‚Üí    fee = await feeConfig.getRequestFee(abiEncodedRequest);
266‚Üí    console.log(`  Attestation fee: ${ethers.formatEther(fee)} C2FLR`);
267‚Üí  } catch {
268‚Üí    // Fallback if fee config call fails
269‚Üí    fee = ethers.parseEther("0.5");
270‚Üí    console.log(`  Using default fee: ${ethers.formatEther(fee)} C2FLR`);
271‚Üí  }
272‚Üí
273‚Üí  // Check wallet balance
274‚Üí  const balance = await signer.provider!.getBalance(signer.address);
275‚Üí  if (balance < fee) {
276‚Üí    throw new Error(
277‚Üí      `Insufficient C2FLR balance!\n` +
278‚Üí        `  Have: ${ethers.formatEther(balance)} C2FLR\n` +
279‚Üí        `  Need: ${ethers.formatEther(fee)} C2FLR\n` +
280‚Üí        `  Get testnet funds at: https://faucet.flare.network/coston2`
281‚Üí    );
282‚Üí  }
283‚Üí
284‚Üí  // Submit the attestation request
285‚Üí  const tx = await fdcHub.requestAttestation(abiEncodedRequest, { value: fee });
286‚Üí  console.log(`  Submitted Coston2 tx: ${tx.hash}`);
287‚Üí
288‚Üí  const receipt = await tx.wait(1);
289‚Üí  if (!receipt || receipt.status !== 1) {
290‚Üí    throw new Error(`Transaction reverted: ${tx.hash}`);
291‚Üí  }
292‚Üí
293‚Üí  // Calculate voting round ID from block timestamp
294‚Üí  const block = await signer.provider!.getBlock(receipt.blockNumber);
295‚Üí  if (!block) throw new Error("Failed to get block");
296‚Üí
297‚Üí  const votingRoundId =
298‚Üí    (BigInt(block.timestamp) - FIRST_VOTING_ROUND_START_TS) / VOTING_EPOCH_DURATION_S;
299‚Üí
300‚Üí  return { coston2TxHash: tx.hash, votingRoundId };
301‚Üí}
302‚Üí
303‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
304‚Üí// Step 3: Wait for Proof Availability (polls DA layer with exponential backoff)
305‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
306‚Üí
307‚Üíasync function waitForProof(
308‚Üí  votingRoundId: bigint,
309‚Üí  abiEncodedRequest: string,
310‚Üí  maxWaitMs: number = 10 * 60 * 1000 // 10 minutes
311‚Üí): Promise<ProofData> {
312‚Üí  const url = `${DA_LAYER_URL}api/v0/fdc/get-proof-round-id-bytes`;
313‚Üí  const startTime = Date.now();
314‚Üí  let delay = 10_000; // start with 10s
315‚Üí  let attempts = 0;
316‚Üí
317‚Üí  // Wait at least 90s for the voting epoch to complete
318‚Üí  console.log(`  Waiting initial ~90s for voting round to complete...`);
319‚Üí  await sleep(95_000);
320‚Üí
321‚Üí  while (true) {
322‚Üí    attempts++;
323‚Üí    const elapsed = Math.floor((Date.now() - startTime) / 1000);
324‚Üí
325‚Üí    if (Date.now() - startTime > maxWaitMs) {
326‚Üí      throw new Error(
327‚Üí        `Timed out after ${elapsed}s waiting for proof (voting round ${votingRoundId}).\n` +
328‚Üí          `The round may not have been finalized or the attestation was not accepted.`
329‚Üí      );
330‚Üí    }
331‚Üí
332‚Üí    try {
333‚Üí      const response = await fetch(url, {
334‚Üí        method: "POST",
335‚Üí        headers: {
336‚Üí          "Content-Type": "application/json",
337‚Üí          "X-API-KEY": API_KEY,
338‚Üí        },
339‚Üí        body: JSON.stringify({
340‚Üí          votingRoundId: Number(votingRoundId),
341‚Üí          requestBytes: abiEncodedRequest,
342‚Üí        }),
343‚Üí      });
344‚Üí
345‚Üí      if (response.ok) {
346‚Üí        const data = await response.json();
347‚Üí        if (data.proof && data.response) {
348‚Üí          console.log(`  Proof available after ${elapsed}s (${attempts} attempts)`);
349‚Üí          return data as ProofData;
350‚Üí        }
351‚Üí      }
352‚Üí
353‚Üí      const status = response.status;
354‚Üí      console.log(
355‚Üí        `  [${elapsed}s] Attempt ${attempts}: proof not yet available (status ${status}), ` +
356‚Üí          `retrying in ${Math.floor(delay / 1000)}s...`
357‚Üí      );
358‚Üí    } catch (err: unknown) {
359‚Üí      const msg = err instanceof Error ? err.message : String(err);
360‚Üí      console.log(
361‚Üí        `  [${elapsed}s] Attempt ${attempts}: ${msg}, retrying in ${Math.floor(delay / 1000)}s...`
362‚Üí      );
363‚Üí    }
364‚Üí
365‚Üí    await sleep(delay);
366‚Üí    delay = Math.min(delay * 1.5, 30_000); // exponential backoff, max 30s
367‚Üí  }
368‚Üí}
369‚Üí
370‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
371‚Üí// Step 4: Verify Proof On-Chain
372‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
373‚Üí
374‚Üíasync function verifyOnChain(
375‚Üí  provider: ethers.JsonRpcProvider,
376‚Üí  proofData: ProofData
377‚Üí): Promise<boolean> {
378‚Üí  const contract = new ethers.Contract(
379‚Üí    FDC_VERIFICATION_ADDRESS,
380‚Üí    FDC_VERIFICATION_ABI,
381‚Üí    provider
382‚Üí  );
383‚Üí
384‚Üí  const resp = proofData.response;
385‚Üí  const body = resp.responseBody;
386‚Üí
387‚Üí  // Construct the proof struct matching IEVMTransaction.Proof
388‚Üí  // Use BigInt for uint64 fields to avoid JS number overflow
389‚Üí  const proofStruct = {
390‚Üí    merkleProof: proofData.proof,
391‚Üí    data: {
392‚Üí      attestationType: resp.attestationType,
393‚Üí      sourceId: resp.sourceId,
394‚Üí      votingRound: BigInt(resp.votingRound),
395‚Üí      lowestUsedTimestamp: BigInt(resp.lowestUsedTimestamp),
396‚Üí      requestBody: {
397‚Üí        transactionHash: resp.requestBody.transactionHash,
398‚Üí        requiredConfirmations: Number(resp.requestBody.requiredConfirmations),
399‚Üí        provideInput: resp.requestBody.provideInput,
400‚Üí        listEvents: resp.requestBody.listEvents,
401‚Üí        logIndices: (resp.requestBody.logIndices || []).map(Number),
402‚Üí      },
403‚Üí      responseBody: {
404‚Üí        blockNumber: BigInt(body.blockNumber),
405‚Üí        timestamp: BigInt(body.timestamp),
406‚Üí        sourceAddress: body.sourceAddress,
407‚Üí        isDeployment: body.isDeployment,
408‚Üí        receivingAddress: body.receivingAddress || ethers.ZeroAddress,
409‚Üí        value: BigInt(body.value),
410‚Üí        input: body.input || "0x",
411‚Üí        status: Number(body.status),
412‚Üí        events: (body.events || []).map((e) => ({
413‚Üí          logIndex: Number(e.logIndex),
414‚Üí          emitterAddress: e.emitterAddress,
415‚Üí          topics: e.topics,
416‚Üí          data: e.data || "0x",
417‚Üí          removed: e.removed || false,
418‚Üí        })),
419‚Üí      },
420‚Üí    },
421‚Üí  };
422‚Üí
423‚Üí  // Call the on-chain verification (view function)
424‚Üí  // This verifies Merkle inclusion against the on-chain root
425‚Üí  return await contract.verifyEVMTransaction(proofStruct);
426‚Üí}
