432‚Üífunction validateAndPrintResults(
433‚Üí  proofData: ProofData,
434‚Üí  expectedTxHash: string,
435‚Üí  expectedStatus: number
436‚Üí): void {
437‚Üí  const resp = proofData.response;
438‚Üí  const body = resp.responseBody;
439‚Üí
440‚Üí  // Check source network (must be testETH for Sepolia)
441‚Üí  const expectedSourceId = toBytes32String("testETH");
442‚Üí  if (resp.sourceId.toLowerCase() !== expectedSourceId.toLowerCase()) {
443‚Üí    throw new Error(
444‚Üí      `Source network mismatch!\n` +
445‚Üí        `  Expected: testETH (${expectedSourceId})\n` +
446‚Üí        `  Got: ${resp.sourceId}`
447‚Üí    );
448‚Üí  }
449‚Üí
450‚Üí  // Check transaction hash matches
451‚Üí  if (resp.requestBody.transactionHash.toLowerCase() !== expectedTxHash.toLowerCase()) {
452‚Üí    throw new Error(
453‚Üí      `Transaction hash mismatch!\n` +
454‚Üí        `  Expected: ${expectedTxHash}\n` +
455‚Üí        `  Got: ${resp.requestBody.transactionHash}`
456‚Üí    );
457‚Üí  }
458‚Üí
459‚Üí  // Check status matches expected
460‚Üí  const actualStatus = Number(body.status);
461‚Üí  if (actualStatus !== expectedStatus) {
462‚Üí    throw new Error(
463‚Üí      `Transaction status mismatch!\n` +
464‚Üí        `  Expected: ${expectedStatus} (${expectedStatus === 1 ? "success" : "reverted"})\n` +
465‚Üí        `  Got: ${actualStatus} (${actualStatus === 1 ? "success" : "reverted"})`
466‚Üí    );
467‚Üí  }
468‚Üí
469‚Üí  // Print verified results
470‚Üí  console.log(`\n${"‚ïê".repeat(70)}`);
471‚Üí  console.log(`  VERIFIED TRANSACTION DETAILS`);
472‚Üí  console.log(`${"‚ïê".repeat(70)}`);
473‚Üí  console.log(`Source chain:      testETH (Sepolia)`);
474‚Üí  console.log(`TX hash:           ${resp.requestBody.transactionHash}`);
475‚Üí  console.log(`Block number:      ${body.blockNumber}`);
476‚Üí  console.log(
477‚Üí    `Timestamp:         ${body.timestamp} (${new Date(Number(body.timestamp) * 1000).toISOString()})`
478‚Üí  );
479‚Üí  console.log(`Status:            ${actualStatus} (${actualStatus === 1 ? "success" : "reverted"})`);
480‚Üí  console.log(`From:              ${body.sourceAddress}`);
481‚Üí  console.log(`To:                ${body.receivingAddress || "(contract creation)"}`);
482‚Üí  console.log(`Value:             ${ethers.formatEther(BigInt(body.value))} ETH`);
483‚Üí  console.log(`Is deployment:     ${body.isDeployment}`);
484‚Üí  console.log(`Events:            ${body.events?.length ?? 0}`);
485‚Üí
486‚Üí  if (body.events?.length) {
487‚Üí    console.log(`\n  Event Details:`);
488‚Üí    body.events.forEach((e, i) => {
489‚Üí      console.log(`    [${i}] emitter=${e.emitterAddress}`);
490‚Üí      console.log(`        topics=${e.topics.length}, logIdx=${e.logIndex}`);
491‚Üí    });
492‚Üí  }
493‚Üí
494‚Üí  console.log(`\nVoting round:      ${resp.votingRound}`);
495‚Üí  console.log(`Merkle proof nodes: ${proofData.proof.length}`);
496‚Üí  console.log(`${"‚ïê".repeat(70)}`);
497‚Üí}
