175‚Üí}
176‚Üí
177‚Üífunction toBytes32String(s: string): string {
178‚Üí  const bytes = new TextEncoder().encode(s);
179‚Üí  if (bytes.length > 32) throw new Error(`String too long for bytes32: ${s}`);
180‚Üí  const hex = Array.from(bytes)
181‚Üí    .map((b) => b.toString(16).padStart(2, "0"))
182‚Üí    .join("");
183‚Üí  return "0x" + hex.padEnd(64, "0");
184‚Üí}
185‚Üí
186‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
187‚Üí// Step 1: Deploy or Connect to FdcAccounting on Sapphire
188‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
189‚Üí
190‚Üíasync function deployOrConnect(
191‚Üí  sapphireSigner: ethers.Signer
192‚Üí): Promise<ethers.Contract> {
193‚Üí  const existingAddress = process.env.ACCOUNTING_CONTRACT_ADDRESS;
194‚Üí
195‚Üí  if (existingAddress) {
196‚Üí    console.log(`  Connecting to existing contract at ${existingAddress}`);
197‚Üí    return new ethers.Contract(existingAddress, FDC_ACCOUNTING_ABI, sapphireSigner);
198‚Üí  }
199‚Üí
200‚Üí  console.log(`  Deploying FdcAccounting to Sapphire testnet...`);
201‚Üí  const factory = new ethers.ContractFactory(
202‚Üí    FDC_ACCOUNTING_ABI,
203‚Üí    FDC_ACCOUNTING_BYTECODE,
204‚Üí    sapphireSigner
205‚Üí  );
206‚Üí
207‚Üí  const signerAddress = await sapphireSigner.getAddress();
208‚Üí  const contract = await factory.deploy(signerAddress);
209‚Üí  await contract.waitForDeployment();
210‚Üí
211‚Üí  const address = await contract.getAddress();
212‚Üí  console.log(`  Deployed at ${address}`);
213‚Üí  console.log(`  Authorized relayer: ${signerAddress}`);
214‚Üí
215‚Üí  return contract as ethers.Contract;
216‚Üí}
217‚Üí
218‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
219‚Üí// Step 2: Prepare Attestation Request
220‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
221‚Üí
222‚Üíasync function prepareAttestationRequest(
223‚Üí  txHash: string,
224‚Üí  requiredConfirmations: number
225‚Üí): Promise<string> {
226‚Üí  const sourceNetwork = FdcSourceNetwork.ETH;
227‚Üí  const sourceId = toBytes32String(sourceNetwork === "ETH" ? "testETH" : sourceNetwork);
228‚Üí  const attestationType = toBytes32String("EVMTransaction");
229‚Üí
230‚Üí  const requestBody = {
231‚Üí    transactionHash: txHash,
232‚Üí    requiredConfirmations: String(requiredConfirmations),
233‚Üí    provideInput: true,
234‚Üí    listEvents: true,
235‚Üí    logIndices: [],
236‚Üí  };
237‚Üí
238‚Üí  const url = `${VERIFIER_BASE_URL}verifier/eth/EVMTransaction/prepareRequest`;
239‚Üí
240‚Üí  const response = await fetch(url, {
241‚Üí    method: "POST",
242‚Üí    headers: {
243‚Üí      "Content-Type": "application/json",
244‚Üí      "X-API-KEY": API_KEY,
245‚Üí    },
246‚Üí    body: JSON.stringify({ attestationType, sourceId, requestBody }),
247‚Üí  });
248‚Üí
249‚Üí  if (!response.ok) {
250‚Üí    const text = await response.text();
251‚Üí    throw new Error(
252‚Üí      `Verifier API error (${response.status}): ${text}\n` +
253‚Üí        `This may indicate the transaction hash is invalid or doesn't exist on Sepolia.`
254‚Üí    );
255‚Üí  }
256‚Üí
257‚Üí  const data = await response.json();
258‚Üí  if (data.status === "INVALID" || !data.abiEncodedRequest) {
259‚Üí    throw new Error(
260‚Üí      `Verifier rejected request: ${JSON.stringify(data)}\n` +
261‚Üí        `Possible causes: invalid tx hash, insufficient confirmations, or tx doesn't exist.`
262‚Üí    );
263‚Üí  }
264‚Üí
265‚Üí  return data.abiEncodedRequest;
266‚Üí}
267‚Üí
268‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
269‚Üí// Step 3: Submit Attestation Request to FdcHub
270‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
271‚Üí
272‚Üíasync function submitAttestationRequest(
273‚Üí  signer: ethers.Wallet,
274‚Üí  abiEncodedRequest: string
275‚Üí): Promise<{ coston2TxHash: string; votingRoundId: bigint }> {
276‚Üí  const fdcHub = new ethers.Contract(FDC_HUB_ADDRESS, FDC_HUB_ABI, signer);
277‚Üí  const feeConfig = new ethers.Contract(
278‚Üí    FDC_FEE_CONFIG_ADDRESS,
279‚Üí    FDC_FEE_CONFIG_ABI,
280‚Üí    signer.provider
281‚Üí  );
282‚Üí
283‚Üí  let fee: bigint;
284‚Üí  try {
285‚Üí    fee = await feeConfig.getRequestFee(abiEncodedRequest);
286‚Üí    console.log(`  Attestation fee: ${ethers.formatEther(fee)} C2FLR`);
287‚Üí  } catch {
288‚Üí    fee = ethers.parseEther("0.5");
289‚Üí    console.log(`  Using default fee: ${ethers.formatEther(fee)} C2FLR`);
290‚Üí  }
291‚Üí
292‚Üí  const balance = await signer.provider!.getBalance(signer.address);
293‚Üí  if (balance < fee) {
294‚Üí    throw new Error(
295‚Üí      `Insufficient C2FLR balance!\n` +
296‚Üí        `  Have: ${ethers.formatEther(balance)} C2FLR\n` +
297‚Üí        `  Need: ${ethers.formatEther(fee)} C2FLR\n` +
298‚Üí        `  Get testnet funds at: https://faucet.flare.network/coston2`
299‚Üí    );
300‚Üí  }
301‚Üí
302‚Üí  const tx = await fdcHub.requestAttestation(abiEncodedRequest, { value: fee });
303‚Üí  console.log(`  Submitted Coston2 tx: ${tx.hash}`);
304‚Üí
305‚Üí  const receipt = await tx.wait(1);
306‚Üí  if (!receipt || receipt.status !== 1) {
307‚Üí    throw new Error(`Transaction reverted: ${tx.hash}`);
308‚Üí  }
309‚Üí
310‚Üí  const block = await signer.provider!.getBlock(receipt.blockNumber);
311‚Üí  if (!block) throw new Error("Failed to get block");
312‚Üí
313‚Üí  const votingRoundId =
314‚Üí    (BigInt(block.timestamp) - FIRST_VOTING_ROUND_START_TS) / VOTING_EPOCH_DURATION_S;
315‚Üí
316‚Üí  return { coston2TxHash: tx.hash, votingRoundId };
317‚Üí}
318‚Üí
319‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
320‚Üí// Step 4: Wait for Proof
321‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
322‚Üí
323‚Üíasync function waitForProof(
324‚Üí  votingRoundId: bigint,
325‚Üí  abiEncodedRequest: string,
326‚Üí  maxWaitMs: number = 10 * 60 * 1000
327‚Üí): Promise<ProofData> {
328‚Üí  const url = `${DA_LAYER_URL}api/v0/fdc/get-proof-round-id-bytes`;
329‚Üí  const startTime = Date.now();
330‚Üí  let delay = 10_000;
331‚Üí  let attempts = 0;
332‚Üí
333‚Üí  console.log(`  Waiting initial ~90s for voting round to complete...`);
334‚Üí  await sleep(95_000);
335‚Üí
336‚Üí  while (true) {
337‚Üí    attempts++;
338‚Üí    const elapsed = Math.floor((Date.now() - startTime) / 1000);
339‚Üí
340‚Üí    if (Date.now() - startTime > maxWaitMs) {
341‚Üí      throw new Error(
342‚Üí        `Timed out after ${elapsed}s waiting for proof (voting round ${votingRoundId}).`
343‚Üí      );
344‚Üí    }
345‚Üí
346‚Üí    try {
347‚Üí      const response = await fetch(url, {
348‚Üí        method: "POST",
349‚Üí        headers: {
350‚Üí          "Content-Type": "application/json",
351‚Üí          "X-API-KEY": API_KEY,
352‚Üí        },
353‚Üí        body: JSON.stringify({
354‚Üí          votingRoundId: Number(votingRoundId),
355‚Üí          requestBytes: abiEncodedRequest,
356‚Üí        }),
357‚Üí      });
358‚Üí
359‚Üí      if (response.ok) {
360‚Üí        const data = await response.json();
361‚Üí        if (data.proof && data.response) {
362‚Üí          console.log(`  Proof available after ${elapsed}s (${attempts} attempts)`);
363‚Üí          return data as ProofData;
364‚Üí        }
365‚Üí      }
366‚Üí
367‚Üí      console.log(
368‚Üí        `  [${elapsed}s] Attempt ${attempts}: proof not yet available (status ${response.status}), ` +
369‚Üí          `retrying in ${Math.floor(delay / 1000)}s...`
370‚Üí      );
371‚Üí    } catch (err: unknown) {
372‚Üí      const msg = err instanceof Error ? err.message : String(err);
373‚Üí      console.log(
374‚Üí        `  [${elapsed}s] Attempt ${attempts}: ${msg}, retrying in ${Math.floor(delay / 1000)}s...`
375‚Üí      );
376‚Üí    }
377‚Üí
378‚Üí    await sleep(delay);
379‚Üí    delay = Math.min(delay * 1.5, 30_000);
380‚Üí  }
381‚Üí}
382‚Üí
383‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
384‚Üí// Step 5: Verify Proof On-Chain
385‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
386‚Üí
387‚Üíasync function verifyOnChain(
388‚Üí  provider: ethers.JsonRpcProvider,
389‚Üí  proofData: ProofData
390‚Üí): Promise<boolean> {
391‚Üí  const contract = new ethers.Contract(
392‚Üí    FDC_VERIFICATION_ADDRESS,
393‚Üí    FDC_VERIFICATION_ABI,
394‚Üí    provider
395‚Üí  );
396‚Üí
397‚Üí  const resp = proofData.response;
398‚Üí  const body = resp.responseBody;
399‚Üí
400‚Üí  const proofStruct = {
401‚Üí    merkleProof: proofData.proof,
402‚Üí    data: {
403‚Üí      attestationType: resp.attestationType,
404‚Üí      sourceId: resp.sourceId,
405‚Üí      votingRound: BigInt(resp.votingRound),
406‚Üí      lowestUsedTimestamp: BigInt(resp.lowestUsedTimestamp),
407‚Üí      requestBody: {
408‚Üí        transactionHash: resp.requestBody.transactionHash,
409‚Üí        requiredConfirmations: Number(resp.requestBody.requiredConfirmations),
410‚Üí        provideInput: resp.requestBody.provideInput,
411‚Üí        listEvents: resp.requestBody.listEvents,
412‚Üí        logIndices: (resp.requestBody.logIndices || []).map(Number),
413‚Üí      },
414‚Üí      responseBody: {
415‚Üí        blockNumber: BigInt(body.blockNumber),
416‚Üí        timestamp: BigInt(body.timestamp),
417‚Üí        sourceAddress: body.sourceAddress,
418‚Üí        isDeployment: body.isDeployment,
419‚Üí        receivingAddress: body.receivingAddress || ethers.ZeroAddress,
420‚Üí        value: BigInt(body.value),
421‚Üí        input: body.input || "0x",
422‚Üí        status: Number(body.status),
423‚Üí        events: (body.events || []).map((e) => ({
424‚Üí          logIndex: Number(e.logIndex),
425‚Üí          emitterAddress: e.emitterAddress,
426‚Üí          topics: e.topics,
427‚Üí          data: e.data || "0x",
428‚Üí          removed: e.removed || false,
429‚Üí        })),
430‚Üí      },
431‚Üí    },
432‚Üí  };
433‚Üí
434‚Üí  return await contract.verifyEVMTransaction(proofStruct);
435‚Üí}
436‚Üí
437‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
438‚Üí// Step 6: Relay to Sapphire
439‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
440‚Üí
441‚Üíasync function relayToSapphire(
442‚Üí  accountingContract: ethers.Contract,
443‚Üí  proofData: ProofData
444‚Üí): Promise<void> {
445‚Üí  const body = proofData.response.responseBody;
446‚Üí  const txHash = proofData.response.requestBody.transactionHash;
447‚Üí  const depositor = body.sourceAddress;
448‚Üí  const value = BigInt(body.value);
449‚Üí
450‚Üí  console.log(`  Relaying deposit:`);
451‚Üí  console.log(`    TX hash:    ${txHash}`);
452‚Üí  console.log(`    Depositor:  ${depositor}`);
453‚Üí  console.log(`    Value:      ${ethers.formatEther(value)} ETH`);
454‚Üí
455‚Üí  const tx = await accountingContract.creditDeposit(txHash, depositor, value);
456‚Üí  console.log(`  Sapphire tx: ${tx.hash}`);
457‚Üí
458‚Üí  const receipt = await tx.wait(1);
459‚Üí  if (!receipt || receipt.status !== 1) {
460‚Üí    throw new Error(`creditDeposit reverted: ${tx.hash}`);
461‚Üí  }
462‚Üí  console.log(`  Deposit credited successfully`);
463‚Üí
464‚Üí  // Query the depositor's balance
465‚Üí  const balance = await accountingContract.getBalanceOf(depositor);
466‚Üí  console.log(`  Depositor balance: ${ethers.formatEther(balance)} ETH`);
467‚Üí}
468‚Üí
469‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
470‚Üí// Main
471‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
472‚Üí
473‚Üíasync function main() {
474‚Üí  const coston2Pk = process.env.COSTON2_PK;
475‚Üí  const sapphirePk = process.env.SAPPHIRE_PK ?? coston2Pk;
476‚Üí  const sepoliaTxHash = process.env.SEPOLIA_TX_HASH;
477‚Üí  const requiredConfirmations = parseInt(process.env.REQUIRED_CONFIRMATIONS ?? "1", 10);
478‚Üí
479‚Üí  if (!coston2Pk) {
480‚Üí    throw new Error(
481‚Üí      "Missing COSTON2_PK in .env\n" +
482‚Üí        "  Fund at: https://faucet.flare.network/coston2"
483‚Üí    );
484‚Üí  }
485‚Üí  if (!sapphirePk) {
486‚Üí    throw new Error(
487‚Üí      "Missing SAPPHIRE_PK in .env\n" +
488‚Üí        "  Fund at: https://faucet.oasis.io/"
489‚Üí    );
490‚Üí  }
491‚Üí  if (!sepoliaTxHash || !/^0x[0-9a-fA-F]{64}$/.test(sepoliaTxHash)) {
492‚Üí    throw new Error(
493‚Üí      "Missing or invalid SEPOLIA_TX_HASH in .env\n" +
494‚Üí        "  Must be 0x + 64 hex chars"
495‚Üí    );
496‚Üí  }
497‚Üí
498‚Üí  // Coston2 provider + signer (for FDC attestation)
499‚Üí  const coston2Provider = new ethers.JsonRpcProvider(COSTON2_RPC);
500‚Üí  const coston2Signer = new ethers.Wallet(coston2Pk, coston2Provider);
501‚Üí
502‚Üí  // Sapphire provider + signer (wrapped for encryption)
503‚Üí  const sapphireProvider = sapphire.wrap(
504‚Üí    new ethers.JsonRpcProvider(SAPPHIRE_TESTNET_RPC)
505‚Üí  );
506‚Üí  const sapphireSigner = sapphire.wrap(
507‚Üí    new ethers.Wallet(sapphirePk, sapphireProvider)
508‚Üí  );
509‚Üí
510‚Üí  console.log(`\n${"‚ïê".repeat(70)}`);
511‚Üí  console.log(`  FDC Trusted Relayer ‚Äî Sapphire Accounting Demo`);
512‚Üí  console.log(`${"‚ïê".repeat(70)}`);
513‚Üí  console.log(`Coston2 wallet:    ${coston2Signer.address}`);
514‚Üí  console.log(`Sapphire wallet:   ${await sapphireSigner.getAddress()}`);
515‚Üí  console.log(`Sepolia TX:        ${sepoliaTxHash}`);
516‚Üí
517‚Üí  // Step 1: Deploy or connect to FdcAccounting on Sapphire
518‚Üí  console.log(`\n${"‚îÄ".repeat(70)}`);
519‚Üí  console.log(`Step 1: Deploy/connect FdcAccounting on Sapphire`);
520‚Üí  console.log(`${"‚îÄ".repeat(70)}`);
521‚Üí  const accountingContract = await deployOrConnect(sapphireSigner);
522‚Üí
523‚Üí  // Step 2: Prepare attestation request
524‚Üí  console.log(`\n${"‚îÄ".repeat(70)}`);
525‚Üí  console.log(`Step 2: Preparing attestation request`);
526‚Üí  console.log(`${"‚îÄ".repeat(70)}`);
527‚Üí  const abiEncodedRequest = await prepareAttestationRequest(sepoliaTxHash, requiredConfirmations);
528‚Üí  console.log(`  Request prepared (${abiEncodedRequest.length} hex chars)`);
529‚Üí
530‚Üí  // Step 3: Submit to FdcHub on Coston2
531‚Üí  console.log(`\n${"‚îÄ".repeat(70)}`);
