201‚Üí
202‚Üí  const tx = await sepoliaSigner.sendTransaction({
203‚Üí    to: sepoliaSigner.address,
204‚Üí    value: amount,
205‚Üí  });
206‚Üí  console.log(`  Sepolia tx: ${tx.hash}`);
