 1‚Üí// SPDX-License-Identifier: MIT
 2‚Üípragma solidity ^0.8.24;
 3‚Üí
 4‚Üícontract FdcAccounting {
 5‚Üí    address public immutable authorizedRelayer;
 6‚Üí
 7‚Üí    /// @dev Private mapping ‚Äî on Sapphire, storage is encrypted and not readable by external observers.
 8‚Üí    mapping(address => uint256) private balances;
 9‚Üí    mapping(bytes32 => bool) private processedTxHashes;
10‚Üí
11‚Üí    event DepositCredited(address indexed depositor, uint256 value, bytes32 txHash);
12‚Üí
13‚Üí    error Unauthorized();
14‚Üí    error AlreadyProcessed(bytes32 txHash);
15‚Üí    error ZeroValue();
16‚Üí
17‚Üí    modifier onlyRelayer() {
18‚Üí        if (msg.sender != authorizedRelayer) revert Unauthorized();
19‚Üí        _;
20‚Üí    }
21‚Üí
22‚Üí    constructor(address _relayer) {
23‚Üí        authorizedRelayer = _relayer;
24‚Üí    }
25‚Üí
26‚Üí    /// @notice Credit a deposit verified via FDC attestation.
27‚Üí    /// @param txHash The Sepolia transaction hash (prevents double-credit).
28‚Üí    /// @param depositor The address to credit.
29‚Üí    /// @param value The deposit amount in wei.
30‚Üí    function creditDeposit(bytes32 txHash, address depositor, uint256 value) external onlyRelayer {
31‚Üí        if (value == 0) revert ZeroValue();
32‚Üí        if (processedTxHashes[txHash]) revert AlreadyProcessed(txHash);
33‚Üí
34‚Üí        processedTxHashes[txHash] = true;
35‚Üí        balances[depositor] += value;
36‚Üí
37‚Üí        emit DepositCredited(depositor, value, txHash);
38‚Üí    }
39‚Üí
40‚Üí    /// @notice Query your own balance.
41‚Üí    function getBalance() external view returns (uint256) {
42‚Üí        return balances[msg.sender];
43‚Üí    }
44‚Üí
45‚Üí    /// @notice Query any user's balance (relayer only).
46‚Üí    function getBalanceOf(address user) external view onlyRelayer returns (uint256) {
47‚Üí        return balances[user];
48‚Üí    }
49‚Üí}
50‚Üí

