182‚Üífunction toBytes32String(s: string): string {
183‚Üí  const bytes = new TextEncoder().encode(s);
184‚Üí  if (bytes.length > 32) throw new Error(`String too long for bytes32: ${s}`);
185‚Üí  const hex = Array.from(bytes)
186‚Üí    .map((b) => b.toString(16).padStart(2, "0"))
187‚Üí    .join("");
188‚Üí  return "0x" + hex.padEnd(64, "0");
189‚Üí}
