195‚Üíasync function sendSepoliaDeposit(
196‚Üí  sepoliaSigner: ethers.Wallet,
197‚Üí  confirmations: number
198‚Üí): Promise<string> {
199‚Üí  const amount = ethers.parseEther(DEPOSIT_AMOUNT);
200‚Üí  console.log(`  Sending ${DEPOSIT_AMOUNT} ETH to self on Sepolia...`);
201‚Üí
202‚Üí  const tx = await sepoliaSigner.sendTransaction({
203‚Üí    to: sepoliaSigner.address,
204‚Üí    value: amount,
205‚Üí  });
206‚Üí  console.log(`  Sepolia tx: ${tx.hash}`);
207‚Üí
208‚Üí  console.log(`  Waiting for ${confirmations} confirmation(s)...`);
209‚Üí  const receipt = await tx.wait(confirmations);
210‚Üí  if (!receipt || receipt.status !== 1) {
211‚Üí    throw new Error(`Sepolia tx reverted: ${tx.hash}`);
212‚Üí  }
213‚Üí  console.log(`  Confirmed in block ${receipt.blockNumber}`);
214‚Üí
215‚Üí  return tx.hash;
216‚Üí}
