251‚Üí// Step 2: Prepare Attestation Request
252‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
253‚Üí
254‚Üíasync function prepareAttestationRequest(
255‚Üí  txHash: string,
256‚Üí  requiredConfirmations: number
257‚Üí): Promise<string> {
258‚Üí  const sourceNetwork = FdcSourceNetwork.ETH;
259‚Üí  const sourceId = toBytes32String(sourceNetwork === "ETH" ? "testETH" : sourceNetwork);
260‚Üí  const attestationType = toBytes32String("EVMTransaction");
261‚Üí
262‚Üí  const requestBody = {
263‚Üí    transactionHash: txHash,
264‚Üí    requiredConfirmations: String(requiredConfirmations),
265‚Üí    provideInput: true,
266‚Üí    listEvents: true,
267‚Üí    logIndices: [],
268‚Üí  };
269‚Üí
270‚Üí  const url = `${VERIFIER_BASE_URL}verifier/eth/EVMTransaction/prepareRequest`;
271‚Üí
272‚Üí  const response = await fetch(url, {
273‚Üí    method: "POST",
274‚Üí    headers: {
275‚Üí      "Content-Type": "application/json",
276‚Üí      "X-API-KEY": API_KEY,
277‚Üí    },
278‚Üí    body: JSON.stringify({ attestationType, sourceId, requestBody }),
279‚Üí  });
280‚Üí
281‚Üí  if (!response.ok) {
282‚Üí    const text = await response.text();
283‚Üí    throw new Error(
284‚Üí      `Verifier API error (${response.status}): ${text}\n` +
285‚Üí        `This may indicate the transaction hash is invalid or doesn't exist on Sepolia.`
286‚Üí    );
287‚Üí  }
288‚Üí
289‚Üí  const data = await response.json();
290‚Üí  if (data.status === "INVALID" || !data.abiEncodedRequest) {
291‚Üí    throw new Error(
292‚Üí      `Verifier rejected request: ${JSON.stringify(data)}\n` +
293‚Üí        `Possible causes: invalid tx hash, insufficient confirmations, or tx doesn't exist.`
294‚Üí    );
295‚Üí  }
296‚Üí
297‚Üí  return data.abiEncodedRequest;
298‚Üí}
299‚Üí
300‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
301‚Üí// Step 3: Submit Attestation Request to FdcHub
302‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
303‚Üí
304‚Üíasync function submitAttestationRequest(
305‚Üí  signer: ethers.Wallet,
306‚Üí  abiEncodedRequest: string
307‚Üí): Promise<{ coston2TxHash: string; votingRoundId: bigint }> {
308‚Üí  const fdcHub = new ethers.Contract(FDC_HUB_ADDRESS, FDC_HUB_ABI, signer);
309‚Üí  const feeConfig = new ethers.Contract(
310‚Üí    FDC_FEE_CONFIG_ADDRESS,
311‚Üí    FDC_FEE_CONFIG_ABI,
312‚Üí    signer.provider
313‚Üí  );
314‚Üí
315‚Üí  let fee: bigint;
316‚Üí  try {
317‚Üí    fee = await feeConfig.getRequestFee(abiEncodedRequest);
318‚Üí    console.log(`  Attestation fee: ${ethers.formatEther(fee)} C2FLR`);
319‚Üí  } catch {
320‚Üí    fee = ethers.parseEther("0.5");
321‚Üí    console.log(`  Using default fee: ${ethers.formatEther(fee)} C2FLR`);
322‚Üí  }
323‚Üí
324‚Üí  const balance = await signer.provider!.getBalance(signer.address);
325‚Üí  if (balance < fee) {
326‚Üí    throw new Error(
327‚Üí      `Insufficient C2FLR balance!\n` +
328‚Üí        `  Have: ${ethers.formatEther(balance)} C2FLR\n` +
329‚Üí        `  Need: ${ethers.formatEther(fee)} C2FLR\n` +
330‚Üí        `  Get testnet funds at: https://faucet.flare.network/coston2`
331‚Üí    );
332‚Üí  }
333‚Üí
334‚Üí  const tx = await fdcHub.requestAttestation(abiEncodedRequest, { value: fee });
335‚Üí  console.log(`  Submitted Coston2 tx: ${tx.hash}`);
336‚Üí
337‚Üí  const receipt = await tx.wait(1);
338‚Üí  if (!receipt || receipt.status !== 1) {
339‚Üí    throw new Error(`Transaction reverted: ${tx.hash}`);
340‚Üí  }
341‚Üí
342‚Üí  const block = await signer.provider!.getBlock(receipt.blockNumber);
343‚Üí  if (!block) throw new Error("Failed to get block");
344‚Üí
345‚Üí  const votingRoundId =
346‚Üí    (BigInt(block.timestamp) - FIRST_VOTING_ROUND_START_TS) / VOTING_EPOCH_DURATION_S;
347‚Üí
348‚Üí  return { coston2TxHash: tx.hash, votingRoundId };
349‚Üí}
350‚Üí
351‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
352‚Üí// Step 4: Wait for Proof
353‚Üí// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
354‚Üí
355‚Üíasync function waitForProof(
356‚Üí  votingRoundId: bigint,
357‚Üí  abiEncodedRequest: string,
358‚Üí  maxWaitMs: number = 10 * 60 * 1000
359‚Üí): Promise<ProofData> {
360‚Üí  const url = `${DA_LAYER_URL}api/v0/fdc/get-proof-round-id-bytes`;
361‚Üí  const startTime = Date.now();
362‚Üí  let delay = 10_000;
363‚Üí  let attempts = 0;
364‚Üí
365‚Üí  console.log(`  Waiting initial ~90s for voting round to complete...`);
366‚Üí  await sleep(95_000);
367‚Üí
368‚Üí  while (true) {
369‚Üí    attempts++;
370‚Üí    const elapsed = Math.floor((Date.now() - startTime) / 1000);
371‚Üí
372‚Üí    if (Date.now() - startTime > maxWaitMs) {
373‚Üí      throw new Error(
374‚Üí        `Timed out after ${elapsed}s waiting for proof (voting round ${votingRoundId}).`
375‚Üí      );
376‚Üí    }
377‚Üí
378‚Üí    try {
379‚Üí      const response = await fetch(url, {
380‚Üí        method: "POST",
381‚Üí        headers: {
382‚Üí          "Content-Type": "application/json",
383‚Üí          "X-API-KEY": API_KEY,
384‚Üí        },
385‚Üí        body: JSON.stringify({
386‚Üí          votingRoundId: Number(votingRoundId),
387‚Üí          requestBytes: abiEncodedRequest,
388‚Üí        }),
389‚Üí      });
390‚Üí
391‚Üí      if (response.ok) {
392‚Üí        const data = await response.json();
393‚Üí        if (data.proof && data.response) {
394‚Üí          console.log(`  Proof available after ${elapsed}s (${attempts} attempts)`);
395‚Üí          return data as ProofData;
396‚Üí        }
397‚Üí      }
398‚Üí
399‚Üí      console.log(
400‚Üí        `  [${elapsed}s] Attempt ${attempts}: proof not yet available (status ${response.status}), ` +
401‚Üí          `retrying in ${Math.floor(delay / 1000)}s...`
402‚Üí      );
403‚Üí    } catch (err: unknown) {
404‚Üí      const msg = err instanceof Error ? err.message : String(err);
405‚Üí      console.log(
406‚Üí        `  [${elapsed}s] Attempt ${attempts}: ${msg}, retrying in ${Math.floor(delay / 1000)}s...`
407‚Üí      );
408‚Üí    }
409‚Üí
410‚Üí    await sleep(delay);
411‚Üí    delay = Math.min(delay * 1.5, 30_000);
412‚Üí  }
413‚Üí}
