```typescript
async function signAndBroadcastWithdrawal(
  accountingContract: ethers.Contract,
  sepoliaProvider: ethers.JsonRpcProvider,
  depositorAddress: string,
  depositAddress: string,
  withdrawalAmount: bigint,
  sepoliaChainId: number
): Promise<void> {
  // Get the encumbered wallet's nonce on Sepolia
  const nonce = await sepoliaProvider.getTransactionCount(depositAddress);
  console.log(`  Encumbered wallet nonce on Sepolia: ${nonce}`);

  // Get current gas price from Sepolia
  const feeData = await sepoliaProvider.getFeeData();
  const gasPrice = feeData.gasPrice ?? ethers.parseUnits("20", "gwei");
  console.log(`  Sepolia gas price: ${ethers.formatUnits(gasPrice, "gwei")} gwei`);

  // Calculate gas cost for simple transfer (21000 gas)
  const gasCost = gasPrice * 21000n;
  console.log(`  Estimated gas cost: ${ethers.formatEther(gasCost)} ETH`);

  // Withdrawal amount is deposit minus gas cost
  const actualWithdrawal = withdrawalAmount - gasCost;
  if (actualWithdrawal <= 0n) {
    throw new Error(
      `Deposit amount (${ethers.formatEther(withdrawalAmount)} ETH) is too small to cover gas ` +
      `(${ethers.formatEther(gasCost)} ETH). Increase DEPOSIT_AMOUNT.`
    );
  }
  console.log(`  Withdrawal amount (after gas): ${ethers.formatEther(actualWithdrawal)} ETH`);

  // Call signWithdrawal on the Sapphire contract
  console.log(`  Requesting signed withdrawal from Sapphire contract...`);
  const signedTx: string = await accountingContract.signWithdrawal(
    depositorAddress,
    actualWithdrawal,
    gasPrice,
    nonce,
    sepoliaChainId
  );
  console.log(`  Got signed tx (${signedTx.length} hex chars)`);

  // Broadcast the signed transaction to Sepolia
  console.log(`  Broadcasting withdrawal tx to Sepolia...`);
  const txResponse = await sepoliaProvider.broadcastTransaction(signedTx);
  console.log(`  Sepolia withdrawal tx: ${txResponse.hash}`);

  console.log(`  Waiting for confirmation...`);
  const receipt = await txResponse.wait(1);
  if (!receipt || receipt.status !== 1) {
    throw new Error(`Withdrawal tx reverted: ${txResponse.hash}`);
  }
  console.log(`  Withdrawal confirmed in block ${receipt.blockNumber}`);

  // Query final balance
  const finalBalance = await accountingContract.getBalanceOf(depositorAddress);
  console.log(`  Final depositor balance: ${ethers.formatEther(finalBalance)} ETH`);
}
```

